import {useState,useEffect} from 'react';
import Layout from "../components/Layout";
import Search from '../components/blog/Search';
import Link from 'next/link';
import HomeCard from '../components/home/HomeCard';
import {getCategories} from '../actions/category';
import {listByCategories} from '../actions/blog';

const Index = ()=>{

    const [values,setValues]=useState({
        
        error:false,
        success:false,
        categories:[],
        reload:false
    
    });

    const [rowValues,setRowValues]=useState({
        categoryArray:['catone','react'],
        row1:[],
        row2:[],
        row3:[],
        reload:false
    
    });

     const {reload,categories,success,error} = values;
     const {row1,row2,row3,categoryArray} = rowValues;

    const loadFunc=()=>{

        getCategories().then(data=>{
            if (data.error) {
                console.log(data.error);
            } else {
                console.log('data',data);
                setValues({...values,categories:data,reload:true});
                
               
            }
        })
       
    }

    useEffect(()=>{
        loadFunc();
        filterCategories();
        // filterCategories('react');
       
    },[reload])

    const filterCategories=()=>{
        const selectCategoryId = categories.find((c)=>{
            if(c.name===categoryArray[0]){
                return c._id
            }
        })
        console.log('selectCategoryId',selectCategoryId);

        if(categoryArray[0]==='catone'){
            listByCategories(selectCategoryId).then(data=>{
                if (data.error) {
                    console.log(data.error);
                } else {
                    setRowValues({...rowValues,row1:data})
                   
                }
            })

        }else if(categoryArray[1]==='react'){
            const selectCategoryId = categories.find((c)=>{
                if(c.name===categoryArray[0]){
                    return c._id
                }
            })
            listByCategories(selectCategoryId).then(data=>{
                if (data.error) {
                    console.log(data.error);
                } else {
                    setRowValues({...rowValues,row2:data})
                   
                }
            })
        }
       
    }

    const showRelatedList = ()=>{
        return  row1?row1.map((i,l)=>{
              return(
                  <div className='col-md-4' key={i}>
                      <article>
                          <HomeCard blog={i} />
                      </article>
                  </div>
              )
          }):'';
      }
   

    return(
        <Layout>
            {/* <h2>Index page</h2>
            <Link href="/signup">
            <a>Signup</a>
            </Link>

            */}
{console.log(row2)}
        <div className="banner">
				<div className="container">
					
					<h2>I'm Banner Heading for This Page</h2>
					
					<p>It is our belief that in order to be most efficient it requires adaptive technology and software our customers can focus on their core business.</p>
				</div>
			</div>
            <Search/>
            {console.log("row1",row1)}
            <div className="container">
                <h4 className="text-center pt-4 pb-4 h2">Health || Nutri blogs</h4>
                    <hr />
                    <div className='row'>{showRelatedList()}</div>
            </div>
           
        </Layout>

        



    )
}


export default Index;
