import { useEffect,useState } from 'react';
import Router from 'next/router';
import Link from 'next/Link';
import dynamic from 'next/dynamic';
import {withRouter} from 'next/router';
import {getCookie,isAuth} from '../../actions/auth';
import {getCategories} from '../../actions/category';
import {getTags}    from '../../actions/tag';
import {createBlog} from '../../actions/blog';
import '../../node_modules/react-quill/dist/quill.snow.css'


const ReactQuill = dynamic(()=>import('react-quill'),{ssr:false});


const BlogCreate=({router})=>{

    const blogfromLS = ()=>{
        if(typeof window !=='undefined'){
            if(localStorage.getItem('blog')){
                return JSON.parse(localStorage.getItem('blog'));

            }else{
                return false;
            }
        }
    }
    const [state, setstate] = useState(blogfromLS())

    const [values, setValue] = useState({
        error:'',
        sizeError:'',
        formData:'',
        title:'',
        hidePublishButton:false
    });
    const [categories,setCategories] = setState([]);
    const [tags,setTags] = setState([]);
    const {error,sizeError,formData,title,hidePublishButton} = values;

    const initCategories =()=>{
        getCategories().then(data=>{
            if(data.error){
                setValues({...values,error:data.error})
            }else{
                setCategories(data);
            }
        })
    }

    const initTags =()=>{
        getTags().then(data=>{
            if(data.error){
                setValues({...values,error:data.error})
            }else{
                setTags(data);
            }
        })
    }
    useEffect(()=>{
        setValue({...values,formData:new FormData()})
        setCategories();
        setTags();

    },[router])

    const publishBlog=(e)=>{
        e.preventDefault();

    }

    
    const handleChange=name=>e=>{
       
        const value = name === 'photo'?e.target.files[0]:e.target.value;
        formData.set(name,value);
        setValue({...values,[name]:value,formData,error:''})
    }

    const handleBody=(e)=>{
        setstate(e);
        formData.set('body',e);
        if(typeof window !== 'undefined'){
            localStorage.setItem('blog',JSON.stringify(e));
        }
    }
    const blogForm=()=>{

        return(
            <form onSubmit={publishBlog}> 
            <div className="form-group">
              <label className="text-muted">Title</label>
              <input type="text"  className="form-control" value={title} onChange={handleChange('title')} />
            </div>
            <div className='form-group'>
              <ReactQuill modules={BlogCreate.modules} formats={BlogCreate.formats} value={state} placeholder="Type your content" onChange={handleBody}/>
            </div>
            <div className='form-group'>
              <button type='submit' className='btn btn-primary'>Publish</button>
            </div>

            </form>
        )
    }

    return(
        <div>
            {blogForm()}
            {JSON.stringify(title)}
        </div>
    )
}

BlogCreate.modules = {
    toolbar: [
        [{ header: '1' }, { header: '2' }, { header: [3, 4, 5, 6] }, { font: [] }],
        [{ size: [] }],
        ['bold', 'italic', 'underline', 'strike', 'blockquote'],
        [{ list: 'ordered' }, { list: 'bullet' }],
        ['link', 'image', 'video'],
        ['clean'],
        ['code-block']
    ]
};

BlogCreate.formats = [
    'header',
    'font',
    'size',
    'bold',
    'italic',
    'underline',
    'strike',
    'blockquote',
    'list',
    'bullet',
    'link',
    'image',
    'video',
    'code-block'
];

export default withRouter(BlogCreate);
