import {useState,useEffect} from 'react';
import Layout from "../components/Layout";
import Search from '../components/blog/Search';
import Link from 'next/link';
import homeCard from '../components/home/homeCard';
import {getCategories} from '../actions/category';

const Index = ()=>{

    const [values,setValues]=useState({
        
        error:false,
        success:false,
        categories:[],
        reload:false
    
    });

     const {reload,categories,success,error} = values;

    const loadFunc=()=>{

        getCategories().then(data=>{
            if (data.error) {
                console.log(data.error);
            } else {
                console.log('data',data);
                setValues({...values,categories:data});
               
            }
        })
       
    }

    useEffect(()=>{
        loadFunc();
       
    },[reload])

    const filterCategories=(category)=>{
       

        const selectCategoryId = categories.filter((category)=>{

            if(category.name==category){
                return category._id
            }
        })
        console.log('selectCategoryId',selectCategoryId);
    }


    return(
        <Layout>
            {/* <h2>Index page</h2>
            <Link href="/signup">
            <a>Signup</a>
            </Link>
            */}

        <div className="banner">
				<div className="container">
					
					<h2>I'm Banner Heading for This Page</h2>
					
					<p>It is our belief that in order to be most efficient it requires adaptive technology and software our customers can focus on their core business.</p>
				</div>
			</div>
            <Search/>
            {filterCategories('catone')}
        </Layout>

        



    )
}


export default Index;
