import {useState} from 'react';
import {signup} from '../../actions/auth';

const SignupComponent=()=>{

    const [values,setValues] = useState({

        name:'',
        email:'',
        password:'',
        error:'0',
        loading:false,
        message:'',
        showForm:true
    })

const {name,email,password,error,loading,message,showForm} = values;

const handleSubmit=(e)=>{
    e.preventDefault();
    console.log('submit');
    console.table({name,email,password,error,loading,message,showForm});
    setValues({...values,loading:true,error:false});
    const user = {name,email,password};

    signup(user).then(data=>{
        if(data.error){
            setValues({...value,error:data.error})
        }else{
            setValues({...values,name:'',email:'',password:'',error:'',loading:false,message:data.message,showForm:false});
        }
    })
    
}

const handleChange=name=>e=>{

    setValues({...values,error:false,[name]:e.target.value});

    console.log(e.target.value,[name]);
}


    const signupForm=()=>{
        return(
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input  value={name} onChange={handleChange('name')} type='text' className='form-value' placeholder='Enter'/>
                </div>
                <div className="form-group">
                    <input value={email} onChange={handleChange('email')} type='email' className='form-value' placeholder='Type you email'/>
                </div>
                <div className="form-group">
                    <input value={password}  onChange={handleChange('password')} type='password' className='form-value' placeholder='Type your password'/>
                </div>
                <div >
                    <button className='btn btn-primary'> Signup</button>
                </div>
            </form>
        )
    }

    return(
        <React.Fragment>
            {signupForm()}
        </React.Fragment>
    ) 
};

export default SignupComponent;
