import Head from 'next/head';
import Link from 'next/link';
import Layout from '../../components/Layout';
import { useState,useEffect } from 'react';
import { singleCategory } from '../../actions/category';
import { API, DOMAIN, APP_NAME, FB_APP_ID } from '../../config';
import Card from '../../components/blog/card';
import renderHTML from 'react-render-html';
import moment from 'moment';

const Category = ({category, blogs, query }) => {

   
         
    const head = () => (
        <Head>
            <title>
                {blogs.title} | {APP_NAME}
            </title>
            <meta name="description" content={blog.mdesc} />
            <link rel="canonical" href={`${DOMAIN}/blogs/${query.slug}`} />
            <meta property="og:title" content={`${blogs.title}| ${APP_NAME}`} />
            <meta property="og:description" content={blogs.mdesc} />
            <meta property="og:type" content="webiste" />
            <meta property="og:url" content={`${DOMAIN}/blogs/${query.slug}`} />
            <meta property="og:site_name" content={`${APP_NAME}`} />

            <meta property="og:image" content={`${API}/blog/photo/${blogs.slug}`} />
            <meta property="og:image:secure_url" ccontent={`${API}/blog/photo/${blogs.slug}`} />
            <meta property="og:image:type" content="image/jpg" />
            <meta property="fb:app_id" content={`${FB_APP_ID}`} />
        </Head>
    );

   
    return (
       
        <React.Fragment>
        {head()}
        <Layout>
            <main>
                <div className="container-fluid text-center">
                    <header>
                        <div className="col-md-12 pt-3">
                            <h1 className="display-4 font-weight-bold">{category.name}</h1>
                            {blogs.map((b, i) => (
                                <div>
                                    <Card key={i} blog={b} />
                                    <hr />
                                </div>
                            ))}
                        </div>
                    </header>
                </div>
            </main>
        </Layout>
    </React.Fragment>
    );
};

Category.getInitialProps = ({ query }) => {
    return singleCategory(query.slug).then(data => {
        if (data.error) {
            console.log(data.error);
        } else {
            // console.log('GET INITIAL PROPS IN SINGLE BLOG', data);
            return { category: data.category, blogs: data.blogs, query };
        }
    });
};

export default Category;
