import {useState,useEffect} from 'react';
import Layout from "../components/Layout";
import Search from '../components/blog/Search';
import Link from 'next/link';
import HomeCard from '../components/home/HomeCard';
import {getCategories} from '../actions/category';
import {listByCategories} from '../actions/blog';

const Index = ({categories})=>{

    // const [values,setValues]=useState({
        
    //     error:false,
    //     success:false,
    //     categories:[],
    //    // reload:false
    
    // });

    const [rowValues,setRowValues]=useState({
        catArray:['catone','react'],
        row1:[],
        reload:false
    
    });

    //  const {categories,success,error} = values;
     const {row1,row2,row3,reload,catArray} = rowValues;

    // const loadFunc=()=>{

    //     getCategories().then(data=>{
    //         if (data.error) {
    //             console.log(data.error);
    //         } else {
    //             console.log('data',data);
    //             setValues({...values,categories:data});
                
               
    //         }
    //     })
       
    // }

    useEffect(()=>{
        //loadFunc();
        filterCategories()
       
    },[reload])

    const filterCategories=()=>{
        let selectCategoryId=[];
        for(let i=0;i<=catArray.length;i++){
            selectCategoryId[i] = categories.find((c)=>{
                if(c.name===catArray[i]){
                    return c._id
                }
            })
            console.log('selectCategoryId',selectCategoryId);
        }
    }

    const getFilterCategories =()=>{

        listByCategories(selectCategoryId).then(data=>{
            if (data.error) {
                console.log(data.error);
            } else {
                setRowValues({...rowValues,row1:data,reload:true})
               
            }
        })
    }

    const showRelatedList = ()=>{
        return  row1?row1.map((i,l)=>{
              return(
                  <div className='col-md-4' key={i}>
                      <article>
                          <HomeCard blog={i} />
                      </article>
                  </div>
              )
          }):'';
      }
   

    return(
        <Layout>
           

        <div className="banner">
				<div className="container">
					
					{/* <h1>I'm Banner Heading for This Page</h1> */}
					
					{/* <p>It is our belief that in order to be most efficient it requires adaptive technology and software our customers can focus on their core business.</p> */}
				</div>
			</div>
            <Search/>
            {console.log("row1",row1)}
            <div className="container">
                <h4 className="text-center pt-4 pb-4 h2">Health || Nutri blogs</h4>
                    <hr />
                    <div className='row'>{showRelatedList()}</div>
            </div>
           
        </Layout>

        



    )
}

Index.getInitialProps = () => {
    return getCategories().then(data => {
        if (data.error) {
            console.log(data.error);
        } else {
            // console.log('GET INITIAL PROPS IN SINGLE BLOG', data);
            return { categories: data };
        }
    });
};

export default Index;
