import { useState,useEffect } from 'react';
import { isAuth } from '../../actions/auth';
import Router from 'next/router';

const Admin=({children})=>{

    useEffect(()=>{

        if(isAuth() && !isAuth().role !==1){
            Router.push('/signin')
        }
    },[])
return(<React.Fragment>
    
        {children}
        
        </React.Fragment>)
}
export default Admin;
