import { useState, useEffect } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import { isAuth, getCookie } from '../../actions/auth';
import { create,getCategories,removeCategory,singleCategory } from '../../actions/category';


const Category=()=>{

    const [values,setValues]=useState({

        name:'',
        error:false,
        success:false,
        categories:[],
        removed:false,
        reload:false
    
    })

    const {name,error,success,categories,removed,reload}=values;
    useEffect(() => {
        
       loadCategories();
    }, [reload])
    
    const loadCategories=()=>{
        getCategories().then(data=>{
            if(data.error){
                console.lof(data.error);
            }else{
                setValues({...values,categories:data});
            }
        })
    }

    const showCategories=()=>{
        return categories.map((c,i)=>{
                return (
                    <button
                    onDoubleClick={() => deleteConfirm(c.slug)}
                    title="Double click to delete"
                    key={i}
                    className="btn btn-outline-primary mr-1 ml-1 mt-3">
                    {c.name}
                </button>
                );
        })
    }


    
    const token=getCookie('token');
       
    const handleChange=e=>{
        e.preventDefault();
       
        setValues({...values,name:e.target.value,error:false,success:false,removed:''})
    } 

    const clickSubmit = e => {
        e.preventDefault();
        // console.log('create category', name);
        create({ name }, token).then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error, success: false });
            } else {
                setValues({ ...values, error: false, success: true, name: '', removed: false, reload: !reload });
            }
        });
    };

    const deleteCategory=(slug)=>{
        removeCategory(slug,token).then(data=>{
            if (data.error) {
                setValues({ ...values, error: data.error, success: false });
            } else {
                setValues({ ...values, error: false, success: false, name: '', removed: !removed, reload: !reload });
            }

        })

        
    }

    const deleteConfirm = (slug)=>{
        let answer = window.confirm("Are you sure you want to delete");
        if(answer){
            deleteCategory(slug);
        }
    }
    const newCategoryForm=()=>(

        <form onSubmit={clickSubmit}>
            <div className='form-group'>
                <label className='text-muted'> Name</label>
                <input type='text' className='form-control' onChange={handleChange} value={name} required/>
            </div>
            <div>
                <button type='submit' className='btn btn-primary'> Create</button>
            </div>
        </form>
    )

    const showSuccess = () => {
        if (success) {
            return <p className="text-success">Category is created</p>;
        }
    };

    const showError = () => {
        if (error) {
            return <p className="text-danger">Category already exist</p>;
        }
    };

    const showRemoved = () => {
        if (removed) {
            return <p className="text-danger">Category is removed</p>;
        }
    };

    const mouseMoveHandler = e => {
        setValues({ ...values, error: false, success: false, removed: '' });
    };

return(

         <React.Fragment>
            {showSuccess()}
            {showError()}
            {showRemoved()}
            <div onMouseMove={mouseMoveHandler}>
                {newCategoryForm()}
                {showCategories()}
            </div>
        </React.Fragment>
)
}
export default Category;
