import {useState} from 'react';

const SignupComponent=()=>{

    const [values,setValues] = useState({

        name:'',
        email:'',
        password:'',
        error:'0',
        loading:false,
        message:'',
        showForm:true
    })

    const {name,email,password,error,loading,message,showForm} = values;

const handleSubmit=(e)=>{
    e.preventDefault();
    console.log('submit');
    console.table({name,email,password,error,loading,message,showForm});
    
}

const handleChange=e=>{

    setValues({...values,error:false,[name]:e.target.value});

    console.log(e);
}


    const signupForm=()=>{
        return(
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input  value={name} onChange={handleChange('name')} type='text' className='form-value' placeholder='Enter'/>
                </div>
                <div className="form-group">
                    <input value={email} onChange={handleChange} type='email' className='form-value' placeholder='Type you email'/>
                </div>
                <div className="form-group">
                    <input value={password}  onChange={handleChange} type='password' className='form-value' placeholder='Type your password'/>
                </div>
                <div >
                    <button className='btn btn-primary'> Signup</button>
                </div>
            </form>
        )
    }

    return(
        <React.Fragment>
            {signupForm()}
        </React.Fragment>
    ) 
};

export default SignupComponent;
